// Get the data from the context
const dess = flow.get('dess')
if (! dess) {
    return
}

const values = Object.values(dess.output.p_b);

// Calculate the sum of all values
const sum = values.reduce((acc, curr) => acc + curr, 0);

// Calculate the average
let average_price = sum / values.length;

// Prevent division by zero
if (average_price === 0) { average_price = 0.000001; }

// Add a flag to each object indicating above, below, or equal to average
dess.parsed = { p_b: [] }
for (const key in dess.output.p_b) {
    if (dess.output.p_b.hasOwnProperty(key)) {
        const value = dess.output.p_b[key];
        const flag = value > average_price ? "above" : value < average_price ? "below" : "equal";
        const offset_perc = (value - average_price)/average_price;
        dess.parsed.p_b[key] = { flag, offset_perc };
    }
}

const d = new Date()

dess.average_price = average_price
dess.price_compared_to_average = dess.parsed.p_b[d.getHours()].flag
dess.price_offset_percentage = dess.parsed.p_b[d.getHours()].offset_perc
dess.negative_price = dess.parsed.p_b[d.getHours()].value <= 0

return msg;

