let dess = flow.get('dess')     

msg.lastValidUpdate = new Date(flow.get('lastValidUpdate')).toLocaleString()
let date = new Date()
msg.hour = date.getHours()
msg.gsmm = dess.output.gsmm[msg.hour]
msg.feed_in = dess.output.feed_in[msg.hour]
msg.idle_b = dess.output.idle_b[msg.hour]

msg.dess_mode = flow.get('dess_mode')
msg.dess_mode_text = ['Off', 'Auto', 'Buy', 'Sell', 'Node-RED'][msg.dess_mode]
if (Object.keys(dess.schedule).length == 24) {
    msg.dess_hours = 24
} else {
    msg.dess_hours = 48
}

msg.estimations = {
    payload: {
        "datasets": [
            {
                label: 'Consumption',
                type: 'line',
                data: Object.values(dess.output.C).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#FA716F",
                fill: false,
                stepped: 'left',
                pointRadius: 1,
                borderWidth: 1
            },
            {
                label: 'Battery',
                type: 'line',
                data: Object.values(dess.output.B).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#4790D0",
                fill: false,
                stepped: 'left',
                pointRadius: 1,
                borderWidth: 1
            },
            {
                label: 'PV yield',
                type: 'line',
                data: Object.values(dess.output.PV).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#F7AB3E",
                backgroundColor: "#f8aa3dAA",
                fill: true,
                stepped: 'left',
                pointRadius: 1,
                borderWidth: 1
            }
        ],
        "labels": Object.keys(dess.schedule).slice(0, msg.dess_hours),
        "B_max": dess.options.b_max
    }
}

msg.schedule = {
    payload: {
        "datasets": [
            {
                label: "Grid Usage",
                data: Object.values(dess.output.g).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#FA716F",
                backgroundColor: "#FA716F",
                fill: true
            },
            {
                label: "Battery usage",
                data: Object.values(dess.output.b).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#4790D0",
                backgroundColor: "#4790D0",
                fill: true
            }],
        "labels": Object.keys(dess.schedule).slice(0, msg.dess_hours)
    }
}

msg.dayaheadprices = {
    payload: {
        "datasets": [
            {
                label: "Buy Price",
                type: 'line',
                data: Object.values(dess.output.p_b).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#FA716F",
                backgroundColor: "#FA716F",
                fill: false,
                stepped: 'left',
                pointRadius: 1,
                borderWidth: 1
            }, {
                label: "Sell Price",
                type: 'line',
                data: Object.values(dess.output.p_s).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#8BC964",
                backgroundColor: "#8BC964",
                fill: false,
                stepped: 'left',
                pointRadius: 1,
                borderWidth: 1
            },],
        "labels": Object.keys(dess.schedule).slice(0, msg.dess_hours)
    }
}

msg.costs = {
    payload: {
        "datasets": [
            {
                label: "Grid costs",
                data: Object.values(dess.output.g_cost).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#FA716F",
                backgroundColor: "#FA716F",
                fill: true
            },
            {
                label: "Battery costs",
                data: Object.values(dess.output.b_cost).slice(0, msg.dess_hours).map(x => x.toFixed(3)),
                borderColor: "#4790D0",
                backgroundColor: "#4790D0",
                fill: true
            }],
        "labels": Object.keys(dess.schedule).slice(0, msg.dess_hours)
    }
}

let b = Object.values(dess.output.b).slice(0, msg.dess_hours).map(x => x.toFixed(3));
let g = Object.values(dess.output.g).slice(0, msg.dess_hours).map(x => x.toFixed(3));
let C = Object.values(dess.output.C).slice(0, msg.dess_hours).map(x => x.toFixed(3));
let PV = Object.values(dess.output.PV).slice(0, msg.dess_hours).map(x => x.toFixed(3));
let n = C.map((c, i) => PV[i] - c);

let to_b = b.map(x => { if (x < 0) { return -x } else { return 0 } });
let from_b = b.map(x => { if (x > 0) { return x } else { return 0 } });
let to_g = g.map(x => { if (x < 0) { return -x } else { return 0 } });
let from_g = g.map(x => { if (x > 0) { return x } else { return 0 } });

let met_need = C.map((c, i) => Math.min(c, PV[i]));

let from_g_to_b = []
let from_b_to_g = []

for (let i = 0; i <= (msg.dess_hours - 1); i++) {
    if (Math.sign(b[i]) * Math.sign(g[i]) >= 0) {
        from_b_to_g.push(0)
        from_g_to_b.push(0)
    } else if (Math.sign(b[i]) == 1 && Math.sign(n[i]) >= 0) {
        from_b_to_g.push(b[i])
        from_g_to_b.push(0)
    } else if (Math.sign(b[i]) == 1 && Math.sign(n[i]) == -1) {
        from_b_to_g.push(-g[i])
        from_g_to_b.push(0)
    } else if (Math.sign(g[i]) == 1 && Math.sign(n[i]) >= 0) {
        from_b_to_g.push(0)
        from_g_to_b.push(g[i])
    } else if (Math.sign(g[i]) == 1 && Math.sign(n[i]) == -1) {
        from_b_to_g.push(0)
        from_g_to_b.push(-b[i])
    }
}

from_g = from_g.map((x, i) => (x - from_g_to_b[i]).toFixed(3))
from_b = from_b.map((x, i) => (x - from_b_to_g[i]).toFixed(3))
to_b = to_b.map((x, i) => (x - from_g_to_b[i]).toFixed(3))
to_g = to_g.map((x, i) => (x - from_b_to_g[i]).toFixed(3))

msg.energy = {
    payload: {
        "datasets": [
            {
                label: "Consumption",
                type: 'line',
                data: C,
                borderColor: "#1066B1",
                backgroundColor: "#1066B1",
                fill: false,
                stepped: 'middle',
                borderWidth: 1
            },
            {
                label: "PV Yield",
                type: 'line',
                data: PV,
                borderColor: "#F7AB3E",
                backgroundColor: "#F7AB3E",
                fill: false,
                stepped: 'middle',
                borderWidth: 1
            },
            {
                label: "",
                type: 'bar',
                data: met_need,
                legend: false,
                borderColor: "#FFFFFF00",
                backgroundColor: "#FFFFFF00",
                fill: true,
                stack: 1,
            },
            {
                label: "From Grid",
                type: 'bar',
                data: from_g,
                borderColor: "#FA716F",
                backgroundColor: "#FA716F",
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },
            {
                label: "To Grid",
                type: 'bar',
                data: to_g,
                borderColor: "#8BC964",
                backgroundColor: "#8BC964",
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },
            {
                label: "From Battery",
                type: 'bar',
                data: from_b,
                borderColor: "#4790D0",
                backgroundColor: "#4790D0",
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },
            {
                label: "To Battery",
                type: 'bar',
                data: to_b,
                borderColor: "#9683EC",
                backgroundColor: "#9683EC",
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },
            {
                label: "From Battery to Grid",
                type: 'bar',
                data: from_b_to_g,
                borderColor: "#4790D0",
                backgroundColor: "#8BC964",
                borderWidth: 4,
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },
            {
                label: "From Grid to Battery",
                type: 'bar',
                data: from_g_to_b,
                borderColor: "#FA716F",
                borderWidth: 4,
                backgroundColor: "#9683EC",
                barPercentage: 0.95,
                fill: true,
                stack: 1,
            },],
        "labels": Object.keys(dess.schedule).slice(0, msg.dess_hours)
    }
}

msg.table = []
for (let schedule = 0; schedule <= 3; schedule++) {
    const currentDateTime = new Date()
    const currentDay = currentDateTime.getDay()
    currentDateTime.setMinutes(schedule*60, 0, 0)
    const scheduleDay = currentDateTime.getDay()
    let currentHour = currentDateTime.getHours()
    if ( currentDay != scheduleDay ) {
        currentHour += 24
    }    
    msg.table.push({
        soc: Number((dess.output.SOC[currentHour]).toFixed(1)),
        feed_in: dess.output.feed_in[currentHour],
        duration: 3600,
        start: currentDateTime.toLocaleString()
    })
}

return msg;

template:
 const arbitraryLine = {
    id: 'arbitraryLine',
    beforeDraw(chart, args, options){
        const { 
            ctx, 
            chartArea: { top, right, bottom, left, width, height, margins}, 
            scales: {x, y}
        } = chart;
        ctx.save()
        const d = new Date();
        let hours = ( options.hours || 24 ) - 1
        ctx.strokeStyle = options.nowBackgroundColor
        ctx.fillStyle = options.nowBackgroundColor
        let widthNow = (width / hours) * (d.getHours()+1)
        let offset = ( options.offset || 0) * ( width / (hours * 2) ) - (( options.offset || 0 ) * .5 * (width / (hours * 4)))
        ctx.fillRect(left + offset, top, widthNow, height)
        ctx.strokeStyle = options.pastBackgroundColor
        ctx.fillStyle = options.pastBackgroundColor
        let widthPast = (width / hours) * (d.getHours())
        ctx.fillRect(left, top, widthPast + offset, height)
        ctx.restore()

    }
}
